import { waitFor, startBy, and, expect } from "../support/acceptance-helper";

import LoginPage from "./login.page.js";

describe("Screening Flow", function() {
  var loginPage;

  before(function() {
    loginPage = new LoginPage();

    loginPage.loginUser();
  });

  context("Screening the list of candidates", function() {
    startBy("Visiting the screening page for 'Testing Job'", function() {
      browser.get("/app/screening/47c244a2-4d53-4a28-97cd-b9ed8ab26c61");

      waitFor(browser.getCurrentUrl, function(url) {
        return url.match(/screening\/47c244a2-4d53-4a28-97cd-b9ed8ab26c61/);
      });
    });

    and("I should see the job title", function() {
      var candidateListPresent = function() {
        return element(by.css("#candidate-list")).isPresent();
      };

      waitFor(candidateListPresent, function(presence){
        return presence;
      });

      waitFor(element(by.css(".page-header")).getText, function(header) {
        expect(header).to.match(/for Testing/);

        return true;
      });
    });

    and("I should see a paginated list of 80 candidates", function() {
      var getCandidateCount = function() {
        return element.all(by.css("ul.candidates li")).count();
      };

      waitFor(getCandidateCount, function(count) {
        expect(count).to.equal(80);

        return true;
      });
    });
  });
});

