import { waitFor, startBy, and, expect } from "../support/acceptance-helper";

import LoginPage from "./login.page.js";
import CandidateProfile from "./candidate-profile.page.js";

describe("Candidate Profile Flow", function() {
  var loginPage;
  var profile;

  before(function() {
    loginPage = new LoginPage();
    profile = new CandidateProfile();

    loginPage.loginUser();
  });

  context("Go into a candidate profile details", function() {
    startBy("Visiting the candidate profile page for 'Gordon Freeman'", function() {
      profile.visit("c89e8e2b-cf16-4709-9c67-60f5c8720515", 31415);

      waitFor(browser.getCurrentUrl, function(url) {
        return url.match("candidates/31415");
      });
    });

    and("I should see candidate profile page", function() {
      var candidateProfilePresent = function() {
        return element(by.css(".candidate-profile")).isPresent();
      };

      waitFor(candidateProfilePresent, function(presence) {
        return presence;
      });
    });

    and("I should see his name", function() {
      var getCandidateName = function() {
        return profile.getCandidateName();
      };

      waitFor(getCandidateName, function(header) {
        expect(header).to.match(/Gordon Freeman/);

        return true;
      });
    });

    and("I should see all side content on left side", function() {
      profile.allElementsOnSideContent().forEach(function(element) {
        expect(element.isPresent()).to.eventually.equal(true);
      });
    });

  });

  context("Go through all tabs", function() {
    startBy("Visiting the candidate profile page for 'Gordon Freeman'", function() {
      profile.visit("c89e8e2b-cf16-4709-9c67-60f5c8720515", 31415);

      waitFor(browser.getCurrentUrl, function(url) {
        return url.match("candidates/31415");
      });
    });

    and("I should see candidate ProfilePage", function() {
      var candidateProfilePresent = function() {
        return element(by.css(".candidate-profile")).isPresent();
      };

      waitFor(candidateProfilePresent, function(presence) {
        return presence;
      });
    });

    and("I should see details on the DetailsPage", function() {
      var applicationDetails = profile.getApplicationDetails().isPresent();

      waitFor(profile.getActiveTabText, function(tabText) {
        expect(tabText).to.match(/Details/);

        return true;
      });

      expect(applicationDetails).to.eventually.equal(true);
    });

    and("go to Resume tab and I should see ResumePage", function() {
      var tabs = profile.getAllTabs();
      var resumeTabButton = tabs.get(2).element(by.css("a"));

      resumeTabButton.click();

      var resumeContent = profile.getResumeContent().isPresent();

      waitFor(profile.getActiveTabText, function(tabText) {
        expect(tabText).to.match(/Resume/);

        return true;
      });

      expect(resumeContent).to.eventually.equal(true);
    });

    and("go to Correspondence tab and I should see correspondence", function() {
      var tabs = profile.getAllTabs();
      var correspondenceTabButton = tabs.get(1).element(by.css("a"));

      correspondenceTabButton.click();

      waitFor(profile.getActiveTabText, function(tabText) {
        expect(tabText).to.match(/Correspondence/);

        return true;
      });
    });

    and("go to Assessments tab and I should see AssessmentsPage", function() {
      var tabs = profile.getAllTabs();
      var assessmentsTabButton = tabs.get(3).element(by.css("a"));

      assessmentsTabButton.click();

      var AssessmentsContent = profile.getAssessmentsContent().isPresent();

      waitFor(profile.getActiveTabText, function(tabText) {
        expect(tabText).to.match(/Assessments/);
        return true;
      });

      expect(AssessmentsContent).to.eventually.equal(true);
    });

    and("go to Interviews tab and I should see InterviewsPage", function() {
      var tabs = profile.getAllTabs();
      var interviewsTabButton = tabs.get(4).element(by.css("a"));

      interviewsTabButton.click();
      var interviewContent = profile.getInterviewContent().isPresent();

      waitFor(profile.getActiveTabText, function(tabText) {
        expect(tabText).to.match(/Interviews/);
        return true;
      });

      expect(interviewContent).to.eventually.equal(true);
    });
  });

  context("Go through all sub tabs", function() {
    startBy("Visiting the candidate profile page for 'Gordon Freeman'", function() {
      profile.visit("c89e8e2b-cf16-4709-9c67-60f5c8720515", 31415);

      waitFor(browser.getCurrentUrl, function(url) {
        return url.match("candidates/31415");
      });
    });

    and("I should see candidate ProfilePage", function() {
      var candidateProfilePresent = function() {
        return element(by.css(".candidate-profile")).isPresent();
      };

      waitFor(candidateProfilePresent, function(presence) {
        return presence;
      });
    });

    and("I should see Note on sub sidebar originally with 3 comments", function() {
      var noteListContent = profile.getNoteListContent().isPresent();

      function getAllNoteListItemCount() {
        return profile.getAllNoteListItem().count();
      }

      waitFor(profile.getActiveSubTabText, function(tabText) {
        expect(tabText).to.match(/Note/);
        return true;
      });

      waitFor(getAllNoteListItemCount, function(count) {
        expect(count).to.equal(3);

        return true;
      });

      expect(noteListContent).to.eventually.equal(true);
    });

    and("click on Recent Activity sub tab and I should see Recent Activity", function() {
      var subTabs = profile.getAllSubTabs();
      var recentActivitySubTabButton = subTabs.get(1).element(by.css("a"));

      recentActivitySubTabButton.click();
      var recentActivityContent = profile.getRecentActivityContent().isPresent();

      function getAllActivityItemCount() {
        return profile.getAllActivityItem().count();
      }

      waitFor(profile.getActiveSubTabText, function(tabText) {
        expect(tabText).to.match(/Recent Activities/);
        return true;
      });

      waitFor(getAllActivityItemCount, function(count) {
        expect(count).to.equal(4);

        return true;
      });

      expect(recentActivityContent).to.eventually.equal(true);
    });

    and("click back into the Note sub tab again, I should see 3 comments", function() {
      var subTabs = profile.getAllSubTabs();
      var noteSubTabButton = subTabs.get(0).element(by.css("a"));

      noteSubTabButton.click();
      var noteListContent = profile.getNoteListContent().isPresent();

      function getAllNoteListItemCount() {
        return profile.getAllNoteListItem().count();
      }

      waitFor(profile.getActiveSubTabText, function(tabText) {
        expect(tabText).to.match(/Note/);
        return true;
      });

      waitFor(getAllNoteListItemCount, function(count) {
        expect(count).to.equal(3);

        return true;
      });

      expect(noteListContent).to.eventually.equal(true);
    });
  });
});

