import { waitFor } from "../support/acceptance-helper";

var CandidateProfilePage = function() {
  this.emailComposerButton = element(by.css("a[name=composer]"));
  this.candidateName = element(by.css(".candidate-activity"));

  function getContainer() {
    var container = element(by.css(".candidate-profile"));

    return {
      container: container,
      mainContent: container.element(by.css(".main-content")),
      subSidebar: container.element(by.css(".sub-sidebar"))
    };
  }

  this.allElementsOnSideContent = function() {
    var container = getContainer().container;

    return [
      container.element(by.css(".candidate-details")),
      container.element(by.css(".stage-navigator")),
      container.element(by.css(".action-buttons")),
      container.element(by.css(".lock-wrap")),
      container.element(by.css(".profile-data")),
      container.element(by.css(".candidate-tagging"))
    ];
  };

  this.visit = function(jobId, id) {
    browser.get(`/app/jobs/${jobId}/candidates/${id}`);
  };

  this.getContainer = function() {
    return getContainer().container;
  };

  this.getCandidateName = function() {
    return element(by.css(".candidate-activity")).getText();
  };

  this.getActiveTabText = function() {
    var mainContent = getContainer().mainContent;
    return mainContent.element(by.css(".is-active")).getText();
  };

  this.getActiveSubTabText = function() {
    var subSidebar = getContainer().subSidebar;
    return subSidebar.element(by.css(".is-active")).getText();
  };

  this.getAllTabs = function() {
    var mainContent = getContainer().mainContent;
    return mainContent.all(by.css(".tabs-menu-item"));
  };

  this.getAllSubTabs = function() {
    var subSidebar = getContainer().subSidebar;
    return subSidebar.all(by.css(".tabs-menu-item"));
  };

  this.getApplicationDetails = function() {
    var mainContent = getContainer().mainContent;
    return mainContent.element(by.css(".application-details"));
  };

  this.getResumeContent = function() {
    var mainContent = getContainer().mainContent;
    return mainContent.element(by.css(".resume-content"));
  };

  this.getAssessmentsContent = function() {
    var mainContent = getContainer().mainContent;
    return mainContent.element(by.css(".assessment-list"));
  };

  this.getInterviewContent = function() {
    var mainContent = getContainer().mainContent;
    return mainContent.element(by.css(".interview-content"));
  };

  this.getRecentActivityContent = function() {
    var subSidebar = getContainer().subSidebar;
    return subSidebar.element(by.css(".recent-activity"));
  };

  this.getAllActivityItem = function() {
    return element.all(by.css(".sub-sidebar .recent-activity .activity.entry"));
  };

  this.getNoteListContent = function() {
    var subSidebar = getContainer().subSidebar;
    return subSidebar.element(by.css(".note-list"));
  };

  this.getAllNoteListItem = function() {
    return element.all(by.css(".sub-sidebar .note-list-item"));
  };

};

module.exports = CandidateProfilePage;

