/* global browser, element, by*/

import { waitFor, startBy, and, expect } from "../support/acceptance-helper";
import CandidateProfilePage from "./candidate-profile.page.js";
import LoginPage from "./login.page.js";

describe("Email Composer", function() {
  var loginPage = new LoginPage();
  var candidateProfilePage = new CandidateProfilePage();

  before(function() {
    loginPage.reset();
    loginPage.loginUser();
  });

  startBy("Visiting Gordon Freeman", function() {
    candidateProfilePage.visit("c89e8e2b-cf16-4709-9c67-60f5c8720515", 31415);
    var candidateNamePresent = function() {
      return candidateProfilePage.candidateName.isPresent();
    };

    waitFor(candidateNamePresent, function(presence) {
      return presence;
    });
  });

  and("clicking on the email composer button I should see the composer", function() {
    candidateProfilePage.emailComposerButton.click();

    expect(element(by.css(".email-composer-panel")).isPresent()).to.eventually.be.true;
  });

  and("I should see the right email in the input", function() {
    expect(element(by.name("recipient")).getAttribute("value")).to.eventually.equal("tadas@pdfcv.com");
  });
});

