var fs = require("fs");
var screenshotCount = 0;
var chai = require("chai");
var chaiAsPromised = require("chai-as-promised");

chai.use(chaiAsPromised);

var waitFor = function(promiseFn, testFn, timeout) {
  // Thank this guy. http://docsplendid.com/archives/209

  browser.wait(function() {
    var deferred = protractor.promise.defer();

    promiseFn().then(function(data) {
      try {
        deferred.fulfill(testFn(data));
      }
      catch (error) {
        if(error.name !== "AssertionError") { throw error; }
        deferred.fulfill(false);
      }
    });

    return deferred.promise;
  }, timeout || Number(process.env.PER_TEST_TIMEOUT) || 8000);
};

var takeScreenshot = function() {
  waitFor(browser.takeScreenshot, function(file) {
    var stream = fs.createWriteStream(`./tmp/screenshot-${screenshotCount}.png`);
    stream.write(new Buffer(file, "base64"));
    screenshotCount++;
    stream.end();

    return true;
  });
};

export default {
  expect: chai.expect,
  startBy: it,
  and: it,
  waitFor: waitFor,
  takeScreenshot: takeScreenshot
};

