/* global browser, element, by */

import { waitFor } from "../support/acceptance-helper";

var LoginPageObject = function() {
  this.container = element(by.css(".login-page"));
  this.form = element(by.css(".login-form"));
  this.emailInput = this.form.element(by.name("email"));
  this.passwordInput = this.form.element(by.name("password"));
  this.submitButton = this.form.element(by.tagName("button"));

  this.visit = function() {
    browser.get("/login");
  };

  this.enterEmail = function(email) {
    this.emailInput.sendKeys(email);
  };

  this.enterPassword = function(password) {
    this.passwordInput.sendKeys(password);
  };

  this.reset = function() {
    browser.executeScript("localStorage.clear()");
  };

  this.allElements = function() {
    return [
      this.form,
      this.emailInput,
      this.passwordInput,
      this.submitButton
    ];
  };

  this.loginUser = function() {
    browser.get("/");
    this.reset();
    this.visit();

    waitFor(browser.getCurrentUrl, function(url) {
      return url.match(/login/);
    });

    this.enterEmail("seph@as.com");
    this.enterPassword("ff7Rocks");

    this.submitButton.click();

    waitFor(browser.getCurrentUrl, function(url) {
      return url.match(/app/);
    });
  };
};

module.exports = LoginPageObject;

