/* global browser, element, by*/

import { waitFor, startBy, and, expect } from "../support/acceptance-helper";

var LoginPage = require("./login.page.js");
var AppPage = require("./app.page.js");

describe("Login Flow", function() {
  var loginPage = new LoginPage();

  before(function() {
    loginPage.reset();
    loginPage.visit();
  });

  it("renders the login form and elements", function() {
    loginPage.allElements().forEach(function(element) {
      expect(element.isPresent()).to.eventually.equal(true);
    });
  });

  context("Using the right credentials to login", function() {
    before(function() {
      loginPage.reset();
      loginPage.visit();
    });

    startBy("entering the right email and password", function() {
      loginPage.enterEmail("seph@as.com");
      loginPage.enterPassword("ff7Rocks");

      expect(loginPage.emailInput.getAttribute("value")).to.eventually.equal("seph@as.com");
      expect(loginPage.passwordInput.getAttribute("value")).to.eventually.equal("ff7Rocks");
    });

    and("clicking the login button brings me into the app", function() {
      loginPage.submitButton.click();

      waitFor(browser.getCurrentUrl, function(url) {
        return url.match(/app/);
      });
    });

    and("clicking the logout button brings me to the login page", function() {
      var appPage = new AppPage();

      browser.actions().mouseMove(appPage.sidebar).perform();

      setTimeout(function(){
        appPage.logoutButton.click();
      }, 400);

      waitFor(browser.getCurrentUrl, function(url) {
        expect(url).to.match(/login/);

        return true;
      });
    });
  });

  context("Using the wrong credentials", function() {
    before(function() {
      loginPage.reset();
      loginPage.visit();
    });

    startBy("entering the right email with the WRONG password", function() {
      loginPage.enterEmail("seph@as.com");
      loginPage.enterPassword("ff7Sucks");

      expect(loginPage.emailInput.getAttribute("value")).to.eventually.equal("seph@as.com");
      expect(loginPage.passwordInput.getAttribute("value")).to.eventually.equal("ff7Sucks");
    });

    and("clicking the login button renders an error message", function() {
      waitFor(loginPage.submitButton.click, function() {
        return element(by.css(".error")).isPresent();
      });
    });
  });

  context("Visiting an in app location while logged out", function() {
    before(function() {
      loginPage.reset();
    });

    it("redirects you to the login page", function() {
      browser.get("app/jobs");

      waitFor(browser.getCurrentUrl, function(url) {
        return url.match(/login/);
      });
    });
  });
});

