/* global browser, element, by*/

import { waitFor, startBy, and } from "../support/acceptance-helper";
import AppPage from "./app.page.js";
import LoginPage from "./login.page.js";

describe("Global Search", function() {
  var appPage;
  var searchInput;

  before(function() {
    var loginPage = new LoginPage();
    appPage = new AppPage();

    loginPage.reset();
    loginPage.loginUser();
  });

  context("Preparing for search", function(){
    startBy("visiting the app", function(){
      appPage.visitJobsPage();

      searchInput = appPage.getSearchInput();
    });

    and("click on the search input", function() {
      var searchBarToBeActive = function() {
        return element(by.css(".search-bar.active")).isPresent();
      };

      browser.sleep(300); // for FOUC

      searchInput.click();

      waitFor(searchBarToBeActive, function(present) {
        return present;
      });
    });
  });

  context("Using the search result", function() {
    var johnNashResult = element(by.cssContainingText(".name", "John Nash"));
    and("and search for John Nash", function() {
      var johnNashResultPresent = function() {
        return johnNashResult.isPresent();
      };

      searchInput.sendKeys("John Nash");

      waitFor(johnNashResultPresent, function(resultPresent) {
        return resultPresent;
      });
    });

    and("click on the result", function() {
      var candidateProfile = element(by.css(".candidate-profile"));
      var candidateProfilePresent = function() {
        return candidateProfile.isPresent();
      };

      johnNashResult.click();

      waitFor(candidateProfilePresent, function(profilePresent) {
        return profilePresent;
      });
    });
  });
});

