import { waitFor, startBy, and, expect } from "../support/acceptance-helper";

import LoginPage from "./login.page.js";
import AppPage from "./app.page.js";

describe("Destroying a candidate", function() {
  var loginPage, appPage;

  before(function() {
    loginPage = new LoginPage();
    appPage = new AppPage();
    loginPage.loginUser();
  });

  context("Destroying a candidate", function() {
    var getDestroyCandidateButton = function() {
      return element(by.css(".destroy-candidate button"));
    };

