      return element.all(by.css("ul.candidates li"));
    };

    startBy("Visiting the user jobs page", function() {
      appPage.visitJobsPage();
    });

    and("Click on the first job and I should see a list of candidates", function() {
      // We need to use a random candidate because other tests might need named candidates like Gordon

      browser.sleep(300); // Rendering of the candidate list
      element.all(by.css("ul.jobs li")).first().click();

      waitFor(browser.getCurrentUrl, function(url) {
        return url.match("/candidates");
      });

      var candidateListCount = function() {
        return getCandidateListItems().count();
      };

      waitFor(candidateListCount, function(count) {
        return count > 1;
      });
    });

    and("click on the first candidate, I should see the candidate's profile", function() {
      getCandidateListItems().first().element(by.css(".name a")).click();

      var candidateDetailsPresent = function() {
        return element(by.css(".candidate-details")).isPresent();
      };

      waitFor(candidateDetailsPresent, function(presence) {
        return presence;
      });
    });

    and("I click on the gear icon to toggle admin actions", function() {
      var toggleAdminButton = element(by.css(".additional-actions .open"));

      var additionalActionsButtonPresent = function() {
        return toggleAdminButton.isPresent();
      };

      waitFor(additionalActionsButtonPresent, function(presence) {
        return presence;
      });

      toggleAdminButton.click();

      var destroyCandidateButtonPresent = function() {
        return getDestroyCandidateButton().isPresent();
      };

      waitFor(destroyCandidateButtonPresent, function(presence) {
        return presence;
      });

      browser.sleep(300); // CSS Animation;
    });

    and("then I click on destroy candidate, I should see a confirmation message", function() {
      getDestroyCandidateButton().click();

      expect(getDestroyCandidateButton().getText()).to.eventually.match(/confirm/i);
    });

    and("then I click on the button again, the candidate should be deleted", function() {
      var initialCount;
      var totalCandidatesElement = element(by.css(".total-candidates"));
      totalCandidatesElement.getText().then(count => initialCount = Number(count));

      getDestroyCandidateButton().click();

      totalCandidatesElement.getText().then(function(afterCount) {
        expect(initialCount - Number(afterCount)).to.equal(1);
      });
    });
  });
});

