      entryFile: entryFile,
      dev: this._getBoolOptionFromQuery(urlObj.query, 'dev', true),
      minify: this._getBoolOptionFromQuery(urlObj.query, 'minify'),
      runModule: this._getBoolOptionFromQuery(urlObj.query, 'runModule', true),
