    case 'vim':
    case 'mvim':
      return [fileName, '+' + lineNumber];
    case 'atom':
    case 'subl':
    case 'sublime':
      return [fileName + ':' + lineNumber];
    case 'joe':
    case 'emacs':
      return ['+' + lineNumber, fileName];
    case 'rmate':
    case 'mate':
      return ['--line', lineNumber, fileName];
  }

  // For all others, drop the lineNumber until we have
  // a mapping above, since providing the lineNumber incorrectly
  // can result in errors or confusing behavior.
  return [fileName];
}

function printInstructions(title) {
  console.log(['', chalk.bgBlue.white.bold(' ' + title + ' '), '  When you see Red Box with stack trace, you can click any ', '  stack frame to jump to the source file. The packager will launch your ', '  editor of choice. It will first look at REACT_EDITOR environment ', '  variable, then at EDITOR. To set it up, you can add something like ', '  REACT_EDITOR=atom to your .bashrc.', ''].join('\n'));
}

var _childProcess = null;
function launchEditor(fileName, lineNumber) {
  if (!fs.existsSync(fileName)) {
    return;
  }

  var editor = process.env.REACT_EDITOR || process.env.EDITOR;
  if (!editor) {
    printInstructions('PRO TIP');
    return;
  }

  var args = [fileName];
  if (lineNumber) {
    args = getArgumentsForLineNumber(editor, fileName, lineNumber);
  }
  console.log('Opening ' + chalk.underline(fileName) + ' with ' + chalk.bold(editor));

  if (_childProcess && isTerminalEditor(editor)) {
    // There's an existing editor process already and it's attached
    // to the terminal, so go kill it. Otherwise two separate editor
    // instances attach to the stdin/stdout which gets confusing.
    _childProcess.kill('SIGKILL');
